WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/22.0/connection.js")}

var updateRecord = new Array(); 
var myquery = "SELECT Id FROM Case WHERE Id = '{!Case.Id}' limit 1";

result = sforce.connection.query(myquery);
records = result.getArray("records");

if(records[0])
{
    var update_Case = records[0];
    update_Case.Reason_Code__c = "a4rb0000000blMk";
    update_Case.Status="Closed";
    updateRecord.push(update_Case);
}

result = sforce.connection.update(updateRecord);
parent.location.href = parent.location.href;

if (result[0].success=='true')
{
    alert('The Case you have selected have been classified as part of the petition for the bike policy, with the following reason: Complaint/Bike Policy/Petition');
} else {
    alert('The Case could not be updated - ' + result[0].errors);
}
