WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/33.0/connection.js")} 
{!REQUIRESCRIPT("/soap/ajax/33.0/apex.js")}



try{ 
	/* Get the record details */
	var updateRecord = new Array(); 
	var myquery = "SELECT Id FROM Disruption_Case__c WHERE Name = '{!Case.CaseNumber}' limit 1"; 
	result = sforce.connection.query(myquery); 
	records = result.getArray("records"); 
	if(records[0]) 
	{
		var update_Case = records[0];

		/* Update the Disruption Case  */
		update_Case.Status__c= "Exchanged";
		updateRecord.push(update_Case); 
		result = sforce.connection.update(updateRecord); 
		sforce.apex.execute
		(
			"Create_Case_Comment",
			"Create_A_CaseComment",
			{
				Parent_Id:"{!Case.Id}",
				content:"---------- Exchange done ----------" 
			}
		);
		parent.location.href = parent.location.href;

	}
}
catch(e){
alert('An Error has Occured. Error: ' + e);
}
