WebLink.url:
{!REQUIRESCRIPT("/support/console/28.0/integration.js")}

var redirectUrl = '/500/e?ent=Case&RecordType=012b0000000M78W&def_account_id={!Account.Id}&retURL=/{!Account.Id}';

if (sforce.console.isInConsole()) {
    sforce.console.getEnclosingTabId(function(enclosingResult){
        sforce.console.getEnclosingPrimaryTabId(function(primaryResult){
            sforce.console.openSubtab(primaryResult.id, redirectUrl, true, '', null);
        });
    });
}
else {
    window.open(redirectUrl,'_self');
}
