ValidationRule.errorConditionFormula:
NOT(
 OR(
  ISBLANK(Departure_Time_Return__c),

  AND(
   IF(ISNUMBER( MID(Departure_Time_Return__c, 1, 2)    ), VALUE(MID(Departure_Time_Return__c, 1, 2))  <24,FALSE),
   IF(ISNUMBER( MID(Departure_Time_Return__c, 4, 2) ), VALUE(MID(Departure_Time_Return__c, 4, 2))<60, FALSE),
   CONTAINS(Departure_Time_Return__c, ":")
  )  
 ) 
)
